mapper.insert:
INSERT INTO CMS_CATEGORY (
    CTGRY_ID, UPPER_CTGRY_ID, CTGRY_NM, USE_AT
) VALUES (
    #{ctgryId}, #{upperCtgryId}, #{ctgryNm}, #{useAt}
)

mapper.update:
UPDATE CMS_CATEGORY
SET UPPER_CTGRY_ID = #{upperCtgryId},
    CTGRY_NM = #{ctgryNm},
    USE_AT = #{useAt}
WHERE CTGRY_ID = #{ctgryId}
