mapper.insert:
INSERT INTO CMS_MEMBER (
    ESNTL_ID, MBER_ID, MBER_NM, PASSWORD, EMAIL_ADRES, MBTLNUM, MBER_STTUS_CODE, DI_KEY
) VALUES (
    #{esntlId}, #{mberId}, #{mberNm}, #{password}, #{emailAdres}, #{mbtlnum}, #{mberSttusCode}, #{diKey}
)

mapper.update:
UPDATE CMS_MEMBER
SET MBER_NM = #{mberNm},
    EMAIL_ADRES = #{emailAdres},
    MBTLNUM = #{mbtlnum},
    MBER_STTUS_CODE = #{mberSttusCode}

        , PASSWORD = #{password}

WHERE ESNTL_ID = #{esntlId}

UPDATE CMS_MEMBER
SET MBER_STTUS_CODE = #{mberSttusCode}
WHERE ESNTL_ID = #{esntlId}

mapper.select:
SELECT ROLE_CODE
FROM CMS_MEMBER_ROLE
WHERE ESNTL_ID = #{esntlId}
