mapper.insert:
INSERT INTO CMS_ROLE (
    ROLE_CODE, ROLE_NM, ROLE_DC, USE_AT
) VALUES (
    #{roleCode}, #{roleNm}, #{roleDc}, #{useAt}
)

mapper.update:
UPDATE CMS_ROLE
SET ROLE_NM = #{roleNm},
    ROLE_DC = #{roleDc},
    USE_AT = #{useAt}
WHERE ROLE_CODE = #{roleCode}

mapper.select:
SELECT MENU_ID
FROM CMS_MENU_ROLE
WHERE ROLE_CODE = #{roleCode}
