mapper.select:
SELECT WIDGET_ID AS widgetId, WIDGET_NM AS widgetNm, WIDGET_TY_CODE AS widgetTyCode, WIDGET_CN AS widgetCn, USE_AT AS useAt, FRST_REGIST_PNTTM AS frstRegistPnttm FROM CMS_WIDGET
AND WIDGET_TY_CODE = #{widgetTyCode}
AND WIDGET_NM LIKE CONCAT('%', #{widgetNm}, '%')
AND USE_AT = #{useAt}
ORDER BY FRST_REGIST_PNTTM DESC

mapper.update:
UPDATE CMS_WIDGET
SET WIDGET_NM = #{widgetNm},
    WIDGET_TY_CODE = #{widgetTyCode},
    WIDGET_CN = #{widgetCn},
    USE_AT = #{useAt}
WHERE WIDGET_ID = #{widgetId}
