mapper.select:
SELECT CONFIG_ID, CONFIG_KEY, CONFIG_VAL, CONFIG_DC, USE_AT, FRST_REGIST_PNTTM, FRST_REGISTER_ID, LAST_UPDT_PNTTM, LAST_UPDUSR_ID FROM CMS_SY_CONFIG WHERE USE_AT = 'Y'
AND CONFIG_KEY = #{configKey}
ORDER BY CONFIG_KEY ASC

mapper.insert:
INSERT INTO CMS_SY_CONFIG (
    CONFIG_ID, CONFIG_KEY, CONFIG_VAL, CONFIG_DC, USE_AT, FRST_REGISTER_ID
) VALUES (
    #{configId}, #{configKey}, #{configVal}, #{configDc}, #{useAt}, #{frstRegisterId}
)

mapper.update:
UPDATE CMS_SY_CONFIG
SET CONFIG_VAL = #{configVal},
    LAST_UPDT_PNTTM = NOW(),
    LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE CONFIG_KEY = #{configKey}
