mapper.insert:
INSERT INTO CMS_FILE_GROUP (
    ATCH_FILE_ID, USE_AT
) VALUES (
    #{atchFileId}, 'Y'
)

INSERT INTO CMS_FILE_DETAIL (
    ATCH_FILE_ID, FILE_SN, FILE_STRE_COURS, STRE_FILE_NM,
    ORIGNL_FILE_NM, FILE_EXTSN, FILE_CN, FILE_SIZE, THUMB_AT,
    FILE_WIDTH, FILE_HEIGHT, FILE_TY, AUTHOR, TAGS, FOLDER_ID
) VALUES (
    #{atchFileId}, #{fileSn}, #{fileStreCours}, #{streFileNm},
    #{orignlFileNm}, #{fileExtsn}, #{fileCn}, #{fileSize}, #{thumbAt},
    #{fileWidth}, #{fileHeight}, #{fileTy}, #{author}, #{tags}, #{folderId}
)

mapper.delete:
DELETE FROM CMS_FILE_DETAIL
WHERE ATCH_FILE_ID = #{atchFileId}

    AND FILE_SN IN

        #{sn}

mapper.insert:
INSERT INTO CMS_MEDIA_FOLDER (
    FOLDER_ID, UPPER_FOLDER_ID, FOLDER_NM, USE_AT
) VALUES (
    #{folderId}, #{upperFolderId}, #{folderNm}, 'Y'
)

mapper.update:
UPDATE CMS_MEDIA_FOLDER
SET FOLDER_NM = #{folderNm},
    UPPER_FOLDER_ID = #{upperFolderId}
WHERE FOLDER_ID = #{folderId}
