mapper.insert:
INSERT INTO CMS_TEMPLATE (
    TMPLAT_ID, TMPLAT_NM, TMPLAT_SE_CODE, TMPLAT_COURS, USE_AT
) VALUES (
    #{tmplatId}, #{tmplatNm}, #{tmplatSeCode}, #{tmplatCours}, #{useAt}
)

mapper.update:
UPDATE CMS_TEMPLATE
SET TMPLAT_NM = #{tmplatNm},
    TMPLAT_SE_CODE = #{tmplatSeCode},
    TMPLAT_COURS = #{tmplatCours},
    USE_AT = #{useAt}
WHERE TMPLAT_ID = #{tmplatId}
